package net.minecraft.world.gen.layer;

import net.minecraft.world.biome.BiomeGenBase;

public class GenLayerAddSnow extends GenLayer
{
    public GenLayerAddSnow(final long par1, final GenLayer par3GenLayer)
    {
        super(par1);
        this.parent = par3GenLayer;
    }

    /**
     * Returns a list of integer values generated by this layer. These may be interpreted as temperatures, rainfall
     * amounts, or biomeList[] indices based on the particular GenLayer subclass.
     */
    public int[] getInts(final int par1, final int par2, final int par3, final int par4)
    {
        final int i1 = par1 - 1;
        final int j1 = par2 - 1;
        final int k1 = par3 + 2;
        final int l1 = par4 + 2;
        final int[] aint = this.parent.getInts(i1, j1, k1, l1);
        final int[] aint1 = IntCache.getIntCache(par3 * par4);

        for (int i2 = 0; i2 < par4; ++i2)
        {
            for (int j2 = 0; j2 < par3; ++j2)
            {
                final int k2 = aint[j2 + 1 + (i2 + 1) * k1];
                this.initChunkSeed((long)(j2 + par1), (long)(i2 + par2));

                if (k2 == 0)
                {
                    aint1[j2 + i2 * par3] = 0;
                }
                else
                {
                    int l2 = this.nextInt(5);

                    if (l2 == 0)
                    {
                        l2 = BiomeGenBase.icePlains.biomeID;
                    }
                    else
                    {
                        l2 = 1;
                    }

                    aint1[j2 + i2 * par3] = l2;
                }
            }
        }

        return aint1;
    }
}
